import React, { Component } from 'react'
import Form from "react-bootstrap/Form"; 
import Button from "react-bootstrap/Button";
import axios from "axios";

export default class CreateCapacitacion extends Component {
  constructor(props) { 
    super(props); 
    this.onChangeCapacitacionId = this.onChangeCapacitacionId.bind(this); 
    this.onChangeCapacitacionDescripcion = this.onChangeCapacitacionDescripcion.bind(this); 
    this.onChangeCapacitacionDuracion = this.onChangeCapacitacionDuracion.bind(this); 
    this.onChangeCapacitacionTipo = this.onChangeCapacitacionTipo.bind(this);
    this.onChangeCapacitacionEncargado = this.onChangeCapacitacionEncargado.bind(this);
    this.onChangeCapacitacionTipoEstudio = this.onChangeCapacitacionTipoEstudio.bind(this);
    this.onSubmit = this.onSubmit.bind(this);   
    
    this.state = { 
      id: "", 
      descripcion: "", 
      duracion: "", 
      tipo: "",
      encargado: "",
      tipoEstudio: "",
    }; 
  } 
  
  onChangeCapacitacionId(e) { 
    this.setState({ id: e.target.value }); 
  } 
  
  onChangeCapacitacionDescripcion(e) { 
    this.setState({ descripcion: e.target.value }); 
  } 
  
  onChangeCapacitacionDuracion(e) { 
    this.setState({ duracion: e.target.value }); 
  } 

  onChangeCapacitacionTipo(e) { 
    this.setState({ tipo: e.target.value }); 
  } 

  onChangeCapacitacionEncargado(e) { 
    this.setState({ encargado: e.target.value }); 
  } 

  onChangeCapacitacionTipoEstudio(e) { 
    this.setState({ tipoEstudio: e.target.value }); 
  } 
  
  onSubmit(e) {
    e.preventDefault();
    const capacitacionObject = {
      id: this.state.id, 
      descripcion: this.state.descripcion, 
      duracion: this.state.duracion, 
      tipo: this.state.tipo,
      encargado: this.state.encargado,
      tipoEstudio: this.state.tipoEstudio,
    };

    axios
      .post("http://localhost:4000/capacitacion/create-capacitacion", capacitacionObject)
      .then((res) => console.log(res.data));
    this.setState({ id: "", descripcion: "", duracion: "", tipo: "", encargado: "", tipoEstudio: ""});
  }

  render() {
    return (
      <div class="form-wrapper">
        <Form onSubmit={this.onSubmit}>
          <Form.Group controlId="id"> 
            <Form.Label>Id</Form.Label> 
            <Form.Control 
              type="text"
              value={this.state.id}
              onChange={this.onChangeCapacitacionId}
            /> 
          </Form.Group>

          <Form.Group controlId="descripcion"> 
            <Form.Label>Descripcion</Form.Label> 
            <Form.Control 
              type="text" 
              value={this.state.descripcion} 
              onChange={this.onChangeCapacitacionDescripcion}
            /> 
          </Form.Group>

          <Form.Group controlId="duracion"> 
            <Form.Label>Duracion</Form.Label> 
            <Form.Control 
              type="text"
              value={this.state.Duracion} 
              onChange={this.onChangeCapacitacionDuracion}
            /> 
          </Form.Group>

          <Form.Group controlId="tipo"> 
            <Form.Label>Tipo</Form.Label> 
            <Form.Control 
              type="text"
              value={this.state.tipo} 
              onChange={this.onChangeCapacitacionTipo}
            /> 
          </Form.Group>

          <Form.Group controlId="encargado"> 
            <Form.Label>Encargado</Form.Label> 
            <Form.Control 
              type="text"
              value={this.state.encargado} 
              onChange={this.onChangeCapacitacionEncargado}
            /> 
          </Form.Group>

          <Form.Group controlId="tipoEstudio"> 
            <Form.Label>Tipo Estudio</Form.Label> 
            <Form.Control 
              type="text"
              value={this.state.tipoEstudio} 
              onChange={this.onChangeCapacitacionTipoEstudio}
            /> 
          </Form.Group>

          <Button 
            variant="danger" 
            size="lg" 
            block="block" 
            type="submit"
            className="mt-4"> 
            Crear Capacitacion 
          </Button>
        </Form>
      </div>
    );
  }
}
