import React, { Component } from "react";
import axios from "axios";
import Table from "react-bootstrap/Table";
import CapacitacionTableRow from "./CapacitacionTableRow";

export default class CapacitacionList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      capacitaciones: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:4000/capacitaciones/")
      .then((res) => {
        this.setState({
          capacitaciones: res.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  DataTable() {
    return this.state.capacitaciones.map((res, i) => {
      return <CapacitacionTableRow obj={res} key={i} />;
    });
  }

  render() {
    return (
      <div className="table-wrapper">
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Id</th>
              <th>Descripcion</th>
              <th>Duracion</th>
              <th>Tipo</th>
              <th>Encargado</th>
              <th>Tipo Estudio</th>
              <th>Accci√≥n</th>
            </tr>
          </thead>
          
        </Table>
      </div>
    );
  }
}
