import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Button from "react-bootstrap/Button";

export default class CapacitacionTableRow extends Component {
  constructor(props) {
    super(props);
    this.deleteCapacitacion = this.deleteCapacitacion.bind(this);
  }

  deleteCapacitacion() {
    axios
      .delete(
        "http://localhost:4000/capacitacion/delete-capacitacion/" + this.props.obj._id
      )
      .then((res) => {
        console.log("Capacitacion successfully deleted!");
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    return (
      <tr>
        <td>{this.props.obj.id}</td>
        <td>{this.props.obj.descripcion}</td>
        <td>{this.props.obj.duracion}</td>
        <td>{this.props.obj.tipo}</td>
        <td>{this.props.obj.encargado}</td>
        <td>{this.props.obj.tipoEstudio}</td>
        <td>
          <Link
            className="edit-link"
            path={"product/:id"}
            to={"/edit-capacitacion/" + this.props.obj._id}
          >
            Editar
          </Link>
          <Button onClick={this.deleteStudent} size="sm" variant="danger">
            Borrar
          </Button>
        </td>
      </tr>
    );
  }
}
