import React, { Component } from "react";
import axios from "axios";
import Table from "react-bootstrap/Table";
import CapacitacionTableRow from "./CapacitacionTableRow";
import { MDBCard, MDBCardHeader, MDBCardBody, MDBCardTitle, MDBCardText, MDBBtn } from 'mdb-react-ui-kit';

export default class CapacitacionList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      capacitaciones: [],
    };
  }

  componentDidMount() {
    axios
      .get("http://localhost:4000/capacitacion/")
      .then((res) => {
        this.setState({
          capacitaciones: res.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  DataTable() {
    return this.state.capacitaciones.map((res, i) => {
      return <CapacitacionTableRow obj={res} key={i} />;
    });
  }

  render() {
    return (

      <MDBCard>
      <MDBCardHeader tag='h3' bg='primary'>
        Featured
      </MDBCardHeader>
      <MDBCardBody text='dark'>
        <MDBCardTitle>Special title treatment</MDBCardTitle>
        <MDBCardText>With supporting text below as a natural lead-in to additional content.</MDBCardText>
        <MDBBtn tag='a' href='#!' color='primary'>
          Go somewhere
        </MDBBtn>
      </MDBCardBody>
    </MDBCard>

    );
  }
}
